          report = new ReportsService($scope.report);
          return report.$save({
            player_id: playerId
          }, function(data) {
            $scope.reports.unshift({
              report: data.report,
              downvoted_by_user: false,
              total_downvotes: 0,
              total_upvotes: 0,
              upvoted_by_user: false,
              user: $scope.report.user,
              vote_difference: 0,
              voted_by_user: false
            });
            if ($scope.report.rating >= 3) {
              $scope.positiveReviewCount += 1;
            } else {
              $scope.negativeReviewCount += 1;
            }
            $scope.report = {
              user_id: $scope.userId,
              player_id: playerId
            };
            $scope.reportedByUser = true;
            return $scope.$emit('submittedReport', 'Review Sucessfully Submitted');
          });
